SELECT COALESCE("t11"."USERNAME", "t11"."USERNAME") AS "USERNAME", "t11"."TOTALUPVOTES", "t11"."TOTALDOWNVOTES", "t11"."POSTID", "t11"."POSTTITLE", "t11"."VIEWCOUNT", "t11"."TOTALANSWERS", "t11"."TOTALCOMMENTS", "t11"."CLOSECOUNT", "t11"."LASTCLOSEDDATE"
FROM (SELECT "t9"."DISPLAYNAME" AS "USERNAME", "t9"."TOTALUPVOTES", "t9"."TOTALDOWNVOTES", "Posts0"."Id" AS "POSTID", "Posts0"."Title" AS "POSTTITLE", "Posts0"."ViewCount" AS "VIEWCOUNT", "t5"."TOTALANSWERS", "t5"."TOTALCOMMENTS", "t2"."CLOSECOUNT", "t2"."LASTCLOSEDDATE"
FROM (SELECT "PostId", COUNT(*) AS "CLOSECOUNT", MAX("CreationDate") AS "LASTCLOSEDDATE"
FROM "STACK"."PostHistory"
WHERE CAST("PostHistoryTypeId" AS INTEGER) = 10
GROUP BY "PostId") AS "t2"
RIGHT JOIN ("STACK"."Posts" AS "Posts0" INNER JOIN (SELECT "t4"."Id" AS "POSTID", "t4"."Title" AS "TITLE", "t4"."ViewCount" AS "VIEWCOUNT", CASE WHEN "t3"."TOTALANSWERS" IS NOT NULL THEN CAST("t3"."TOTALANSWERS" AS BIGINT) ELSE 0 END AS "TOTALANSWERS", CASE WHEN "t3"."TOTALCOMMENTS" IS NOT NULL THEN CAST("t3"."TOTALCOMMENTS" AS BIGINT) ELSE 0 END AS "TOTALCOMMENTS"
FROM (SELECT "s1"."ParentId", COUNT(*) AS "TOTALANSWERS", COUNT(DISTINCT "Comments"."Id") AS "TOTALCOMMENTS"
FROM "STACK"."Comments"
RIGHT JOIN "s1" ON "Comments"."PostId" = "s1"."Id"
GROUP BY "s1"."ParentId") AS "t3"
RIGHT JOIN (SELECT *
FROM "STACK"."Posts"
WHERE CAST("PostTypeId" AS INTEGER) = 1) AS "t4" ON "t3"."ParentId" = "t4"."Id") AS "t5" ON "Posts0"."Id" = "t5"."POSTID" INNER JOIN (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "TOTALUPVOTES", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "TOTALDOWNVOTES"
FROM "STACK"."Votes"
RIGHT JOIN "STACK"."Users" ON "Votes"."UserId" = "Users"."Id"
GROUP BY "Users"."Id", "Users"."DisplayName"
HAVING SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) > SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END)) AS "t9" ON "Posts0"."OwnerUserId" = "t9"."USERID") ON "t2"."PostId" = "Posts0"."Id"
ORDER BY "Posts0"."ViewCount" DESC NULLS FIRST, "t9"."TOTALUPVOTES" DESC NULLS FIRST) AS "t11"