SELECT COALESCE("t0"."USERID", "t0"."USERID") AS "USERID", "t0"."TOTALBADGES", "t0"."GOLDBADGES", "t0"."SILVERBADGES", "t0"."BRONZEBADGES", "t4"."USERID" AS "USERID0", "t4"."DISPLAYNAME", "t4"."TOTALPOSTS", "t4"."TOTALQUESTIONS", "t4"."TOTALANSWERS", "t4"."AVERAGEPOSTSCORE", "t4"."POSTRANK"
FROM (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t0"
RIGHT JOIN (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT("Posts"."Id") AS "TOTALPOSTS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "TOTALQUESTIONS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "TOTALANSWERS", AVG("Posts"."Score") AS "AVERAGEPOSTSCORE", RANK() OVER (ORDER BY COUNT("Posts"."Id") DESC NULLS FIRST) AS "POSTRANK"
FROM "STACK"."Posts"
RIGHT JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id"
GROUP BY "Users"."Id", "Users"."DisplayName"
HAVING COUNT("Posts"."Id") > 0) AS "t4" ON "t0"."USERID" = "t4"."USERID"