SELECT COALESCE("USERID", "USERID") AS "USERID", "DISPLAYNAME", "TOTALPOSTS", "TOTALQUESTIONS", "TOTALANSWERS", "AVERAGEPOSTSCORE", "TOTALBADGES", "GOLDBADGES", "SILVERBADGES", "BRONZEBADGES", "ACTIVITYLEVEL"
FROM (SELECT "USERID0" AS "USERID", "DISPLAYNAME", "TOTALPOSTS", "TOTALQUESTIONS", "TOTALANSWERS", "AVERAGEPOSTSCORE", CASE WHEN "TOTALBADGES" IS NOT NULL THEN CAST("TOTALBADGES" AS BIGINT) ELSE 0 END AS "TOTALBADGES", CASE WHEN "GOLDBADGES" IS NOT NULL THEN CAST("GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "SILVERBADGES" IS NOT NULL THEN CAST("SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "BRONZEBADGES" IS NOT NULL THEN CAST("BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", CASE WHEN "TOTALPOSTS" > 100 THEN 'High Activity    ' WHEN "TOTALPOSTS" >= 50 AND "TOTALPOSTS" <= 100 THEN 'Moderate Activity' ELSE 'Low Activity     ' END AS "ACTIVITYLEVEL"
FROM "s1"
ORDER BY "TOTALPOSTS" DESC NULLS FIRST
OFFSET 0 ROWS
FETCH NEXT 10 ROWS ONLY) AS "t7"