SELECT COALESCE("t10"."USERNAME", "t10"."USERNAME") AS "USERNAME", "t10"."POSTSCREATED", "t10"."TOTALSCORE", "t10"."AVGVIEWCOUNT", "t10"."POSTTITLE", "t10"."POSTHISTORYTYPEID", "t10"."ACTIONDATE", "t10"."ACTIONTYPE", "t10"."POSTTAG", "t10"."TAGPOSTCOUNT", "t10"."TAGTOTALVIEWS"
FROM (SELECT "t7"."DISPLAYNAME" AS "USERNAME", "t7"."POSTSCREATED", "t7"."TOTALSCORE", "t7"."AVGVIEWCOUNT", "t7"."Title" AS "POSTTITLE", "t7"."POSTHISTORYTYPEID", "t7"."CREATIONDATE" AS "ACTIONDATE", CASE WHEN CAST("t7"."POSTHISTORYTYPEID" AS INTEGER) = 10 THEN 'Closed      ' WHEN CAST("t7"."POSTHISTORYTYPEID" AS INTEGER) = 11 THEN 'Reopened    ' WHEN CAST("t7"."POSTHISTORYTYPEID" AS INTEGER) = 12 THEN 'Deleted     ' ELSE 'Other Action' END AS "ACTIONTYPE", "t7"."TagName" AS "POSTTAG", "t8"."POSTCOUNT" AS "TAGPOSTCOUNT", "t8"."TOTALVIEWS" AS "TAGTOTALVIEWS"
FROM (SELECT "t6"."USERID", "t6"."DISPLAYNAME", "t6"."POSTSCREATED", "t6"."TOTALSCORE", "t6"."AVGVIEWCOUNT", "t2"."POSTID", "t2"."POSTHISTORYTYPEID", "t2"."CREATIONDATE", "t2"."PREVIOUSACTION", "t2"."ROWNUM", "Posts0"."Id", "Posts0"."PostTypeId", "Posts0"."AcceptedAnswerId", "Posts0"."ParentId", "Posts0"."CreationDate", "Posts0"."Score", "Posts0"."ViewCount", "Posts0"."Body", "Posts0"."OwnerUserId", "Posts0"."OwnerDisplayName", "Posts0"."LastEditorUserId", "Posts0"."LastEditorDisplayName", "Posts0"."LastEditDate", "Posts0"."LastActivityDate", "Posts0"."Title", "Posts0"."Tags", "Posts0"."AnswerCount", "Posts0"."CommentCount", "Posts0"."FavoriteCount", "Posts0"."ClosedDate", "Posts0"."CommunityOwnedDate", "Posts0"."ContentLicense", "Tags0"."Id" AS "Id0", "Tags0"."TagName", "Tags0"."Count", "Tags0"."ExcerptPostId", "Tags0"."WikiPostId", "Tags0"."IsModeratorOnly", "Tags0"."IsRequired"
FROM "STACK"."Tags" AS "Tags0"
RIGHT JOIN ("STACK"."Posts" AS "Posts0" INNER JOIN ((SELECT "PostId" AS "POSTID", "PostHistoryTypeId" AS "POSTHISTORYTYPEID", "CreationDate" AS "CREATIONDATE", CAST(NULL AS VARCHAR(400) CHARACTER SET "ISO-8859-1") AS "PREVIOUSACTION", ROW_NUMBER() OVER (PARTITION BY "PostId" ORDER BY "CreationDate" DESC NULLS FIRST) AS "ROWNUM"
FROM "STACK"."PostHistory"
WHERE CAST("PostHistoryTypeId" AS INTEGER) IN (10, 11, 12)) AS "t2" INNER JOIN (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT("Posts1"."Id") AS "POSTSCREATED", SUM(CASE WHEN "Posts1"."Score" IS NOT NULL THEN CAST("Posts1"."Score" AS INTEGER) ELSE 0 END) AS "TOTALSCORE", AVG(CASE WHEN "Posts1"."ViewCount" IS NOT NULL THEN CAST("Posts1"."ViewCount" AS INTEGER) ELSE 0 END) AS "AVGVIEWCOUNT"
FROM "STACK"."Posts" AS "Posts1"
RIGHT JOIN "STACK"."Users" ON "Posts1"."OwnerUserId" = "Users"."Id"
GROUP BY "Users"."Id", "Users"."DisplayName"
HAVING SUM(CASE WHEN "Posts1"."Score" IS NOT NULL THEN CAST("Posts1"."Score" AS INTEGER) ELSE 0 END) > 0) AS "t6" ON "t2"."POSTID" = "t6"."USERID") ON "Posts0"."Id" = "t2"."POSTID") ON "Posts0"."Tags" LIKE '%' || "Tags0"."TagName" || '%') AS "t7"
LEFT JOIN (SELECT "TAGNAME", COUNT("Id0") AS "POSTCOUNT", SUM("FD_COL_2") AS "TOTALVIEWS"
FROM "s1"
GROUP BY "TAGNAME") AS "t8" ON "t7"."TagName" = "t8"."TAGNAME"
ORDER BY "t7"."TOTALSCORE" DESC NULLS FIRST, "t7"."CREATIONDATE" DESC NULLS FIRST) AS "t10"