SELECT COALESCE("t"."UserId", "t"."UserId") AS "USERID", "t"."TOTALEDITS", "t"."LASTEDITDATE", "t6"."USERID" AS "USERID0", "t6"."DISPLAYNAME", "t6"."REPUTATION", "t6"."TOTALPOSTS", "t6"."TOTALQUESTIONS", "t6"."TOTALANSWERS", "t6"."ACCEPTEDANSWERS", "t6"."USERID0" AS "USERID00", "t6"."GOLDBADGES", "t6"."SILVERBADGES", "t6"."BRONZEBADGES"
FROM (SELECT "UserId", COUNT(*) AS "TOTALEDITS", MAX("CreationDate") AS "LASTEDITDATE"
FROM "STACK"."PostHistory"
GROUP BY "UserId") AS "t"
RIGHT JOIN (SELECT "t5"."USERID", "t5"."DISPLAYNAME", "t5"."REPUTATION", "t5"."TOTALPOSTS", "t5"."TOTALQUESTIONS", "t5"."TOTALANSWERS", "t5"."ACCEPTEDANSWERS", "t1"."USERID" AS "USERID0", "t1"."GOLDBADGES", "t1"."SILVERBADGES", "t1"."BRONZEBADGES"
FROM (SELECT "UserId" AS "USERID", COUNT(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "GOLDBADGES", COUNT(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "SILVERBADGES", COUNT(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE NULL END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t1"
RIGHT JOIN (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", "Users"."Reputation" AS "REPUTATION", COUNT(DISTINCT "Posts"."Id") AS "TOTALPOSTS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "TOTALQUESTIONS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "TOTALANSWERS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 AND "Posts"."AcceptedAnswerId" IS NOT NULL THEN 1 ELSE 0 END) AS "ACCEPTEDANSWERS"
FROM "STACK"."Posts"
RIGHT JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id"
WHERE "Users"."Reputation" > 100
GROUP BY "Users"."Id", "Users"."DisplayName", "Users"."Reputation") AS "t5" ON "t1"."USERID" = "t5"."USERID") AS "t6" ON "t"."UserId" = "t6"."USERID"