SELECT COALESCE("USERID", "USERID") AS "USERID", "DISPLAYNAME", "REPUTATION", "POSTCOUNT", "POSITIVEPOSTS", "NEGATIVEPOSTS", "POPULARPOSTS", "AVERAGEVIEWS", "BADGECOUNT", "GOLDBADGES", "SILVERBADGES", "BRONZEBADGES"
FROM (SELECT "USERID0" AS "USERID", "DISPLAYNAME", "REPUTATION", "POSTCOUNT", "POSITIVEPOSTS", "NEGATIVEPOSTS", "POPULARPOSTS", "AVERAGEVIEWS", CASE WHEN "BADGECOUNT" IS NOT NULL THEN CAST("BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "GOLDBADGES" IS NOT NULL THEN CAST("GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "SILVERBADGES" IS NOT NULL THEN CAST("SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "BRONZEBADGES" IS NOT NULL THEN CAST("BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES"
FROM "s1"
ORDER BY "REPUTATION" DESC NULLS FIRST, "POSTCOUNT" DESC NULLS FIRST
FETCH NEXT 50 ROWS ONLY) AS "t7"