FROM (SELECT "t8"."USERID", "t8"."DISPLAYNAME", "t8"."REPUTATION", "t8"."VIEWS", "t8"."UPVOTES", "t8"."DOWNVOTES", "t8"."POSTCOUNT", "t8"."QUESTIONCOUNT", "t8"."ANSWERCOUNT", "t8"."CLOSEDPOSTCOUNT", CASE WHEN "t8"."BADGECOUNT" IS NOT NULL THEN CAST("t8"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "GOLDBADGECOUNT", CASE WHEN "t3"."EDITCOUNT" IS NOT NULL THEN CAST("t3"."EDITCOUNT" AS BIGINT) ELSE 0 END AS "EDITCOUNT"
FROM (SELECT "UserId", COUNT(*) AS "EDITCOUNT"
