RIGHT JOIN (SELECT "t7"."USERID", "t7"."DISPLAYNAME", "t7"."REPUTATION", "t7"."VIEWS", "t7"."UPVOTES", "t7"."DOWNVOTES", "t7"."POSTCOUNT", "t7"."QUESTIONCOUNT", "t7"."ANSWERCOUNT", "t7"."CLOSEDPOSTCOUNT", "t5"."UserId" AS "USERID0", "t5"."BADGECOUNT"
FROM (SELECT "UserId", COUNT(*) AS "BADGECOUNT"
FROM "STACK"."Badges"
WHERE CAST("Class" AS INTEGER) = 1
GROUP BY "UserId") AS "t5"
RIGHT JOIN (SELECT ANY_VALUE("Id") AS "USERID", "DISPLAYNAME", "REPUTATION", "VIEWS", "UPVOTES", "DOWNVOTES", COUNT(DISTINCT "Id0") AS "POSTCOUNT", SUM("FD_COL_7") AS "QUESTIONCOUNT", SUM("FD_COL_8") AS "ANSWERCOUNT", SUM("FD_COL_9") AS "CLOSEDPOSTCOUNT"
FROM "s1"
