SELECT COALESCE("t0"."USERID", "t0"."USERID") AS "USERID", "t0"."CLOSEREOPENCOUNT", "t0"."EDITSUGGESTIONCOUNT", "t7"."USERID" AS "USERID0", "t7"."DISPLAYNAME", "t7"."TOTALPOSTS", "t7"."QUESTIONCOUNT", "t7"."ANSWERCOUNT", "t7"."TOTALVIEWS", "t7"."TOTALSCORE", "t7"."LASTPOSTDATE", "t7"."USERID0" AS "USERID00", "t7"."GOLDBADGES", "t7"."SILVERBADGES", "t7"."BRONZEBADGES"
FROM (SELECT "UserId" AS "USERID", COUNT(CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) IN (10, 11) THEN 1 ELSE NULL END) AS "CLOSEREOPENCOUNT", COUNT(CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) IN (24, 25) THEN 1 ELSE NULL END) AS "EDITSUGGESTIONCOUNT"
FROM "STACK"."PostHistory"
GROUP BY "UserId") AS "t0"
RIGHT JOIN (SELECT "t6"."USERID", "t6"."DISPLAYNAME", "t6"."TOTALPOSTS", "t6"."QUESTIONCOUNT", "t6"."ANSWERCOUNT", "t6"."TOTALVIEWS", "t6"."TOTALSCORE", "t6"."LASTPOSTDATE", "t2"."USERID" AS "USERID0", "t2"."GOLDBADGES", "t2"."SILVERBADGES", "t2"."BRONZEBADGES"
FROM (SELECT "UserId" AS "USERID", COUNT(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "GOLDBADGES", COUNT(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "SILVERBADGES", COUNT(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE NULL END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t2"
RIGHT JOIN (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT("Posts"."Id") AS "TOTALPOSTS", COUNT(DISTINCT CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 THEN "Posts"."Id" ELSE NULL END) AS "QUESTIONCOUNT", COUNT(DISTINCT CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 2 THEN "Posts"."Id" ELSE NULL END) AS "ANSWERCOUNT", SUM("Posts"."ViewCount") AS "TOTALVIEWS", SUM("Posts"."Score") AS "TOTALSCORE", MAX("Posts"."CreationDate") AS "LASTPOSTDATE"
FROM "STACK"."Posts"
RIGHT JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id"
GROUP BY "Users"."Id", "Users"."DisplayName"
HAVING COUNT("Posts"."Id") > 0) AS "t6" ON "t2"."USERID" = "t6"."USERID") AS "t7" ON "t0"."USERID" = "t7"."USERID"