SELECT COALESCE("t"."Id", "t"."Id") AS "Id", "t"."DisplayName" AS "DISPLAYNAME", "t"."Reputation" AS "REPUTATION", "t"."CreationDate" AS "CREATIONDATE", "t"."Id0", CASE WHEN CAST("t"."PostTypeId" AS INTEGER) = 1 THEN "t"."Id0" ELSE NULL END AS "FD_COL_5", CASE WHEN CAST("t"."PostTypeId" AS INTEGER) = 2 THEN "t"."Id0" ELSE NULL END AS "FD_COL_6", CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END AS "FD_COL_7", CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END AS "FD_COL_8"
