SELECT COALESCE("t10"."USERID", "t10"."USERID") AS "USERID", "t10"."DISPLAYNAME", "t10"."REPUTATION", "t10"."TOTALPOSTS", "t10"."TOTALQUESTIONS", "t10"."TOTALANSWERS", "t10"."TOTALUPVOTES", "t10"."TOTALDOWNVOTES", "t10"."TOTALBADGES", "t10"."GOLDBADGES", "t10"."SILVERBADGES", "t10"."BRONZEBADGES", "t10"."TOTALEDITS", "t10"."TOTALCLOSEDPOSTS"
FROM (SELECT "t8"."USERID", "t8"."DISPLAYNAME", "t8"."REPUTATION", "t8"."TOTALPOSTS", "t8"."TOTALQUESTIONS", "t8"."TOTALANSWERS", "t8"."TOTALUPVOTES", "t8"."TOTALDOWNVOTES", CASE WHEN "t8"."TOTALBADGES" IS NOT NULL THEN CAST("t8"."TOTALBADGES" AS BIGINT) ELSE 0 END AS "TOTALBADGES", CASE WHEN "t8"."GOLDBADGES" IS NOT NULL THEN CAST("t8"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "t8"."SILVERBADGES" IS NOT NULL THEN CAST("t8"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "t8"."BRONZEBADGES" IS NOT NULL THEN CAST("t8"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", CASE WHEN "t3"."TOTALEDITS" IS NOT NULL THEN CAST("t3"."TOTALEDITS" AS BIGINT) ELSE 0 END AS "TOTALEDITS", CASE WHEN "t3"."TOTALCLOSEDPOSTS" IS NOT NULL THEN CAST("t3"."TOTALCLOSEDPOSTS" AS BIGINT) ELSE 0 END AS "TOTALCLOSEDPOSTS"
FROM (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALEDITS", COUNT(DISTINCT CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) = 10 THEN "Id" ELSE NULL END) AS "TOTALCLOSEDPOSTS"
FROM "STACK"."PostHistory"
GROUP BY "UserId") AS "t3"
RIGHT JOIN (SELECT "t7"."USERID", "t7"."DISPLAYNAME", "t7"."REPUTATION", "t7"."TOTALPOSTS", "t7"."TOTALANSWERS", "t7"."TOTALQUESTIONS", "t7"."TOTALUPVOTES", "t7"."TOTALDOWNVOTES", "t5"."USERID" AS "USERID0", "t5"."TOTALBADGES", "t5"."GOLDBADGES", "t5"."SILVERBADGES", "t5"."BRONZEBADGES"
FROM (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t5"
RIGHT JOIN (SELECT ANY_VALUE("Id") AS "USERID", "DISPLAYNAME", "REPUTATION", COUNT(DISTINCT "Id0") AS "TOTALPOSTS", COUNT(DISTINCT "FD_COL_4") AS "TOTALANSWERS", COUNT(DISTINCT "FD_COL_5") AS "TOTALQUESTIONS", SUM("FD_COL_6") AS "TOTALUPVOTES", SUM("FD_COL_7") AS "TOTALDOWNVOTES"
FROM "s1"
GROUP BY "Id", "DISPLAYNAME", "REPUTATION") AS "t7" ON "t5"."USERID" = "t7"."USERID") AS "t8" ON "t3"."USERID" = "t8"."USERID"
ORDER BY "t8"."REPUTATION" DESC NULLS FIRST
FETCH NEXT 50 ROWS ONLY) AS "t10"