SELECT COALESCE("t0"."USERID", "t0"."USERID") AS "USERID", "t0"."TOTALEDITS", "t0"."TITLEANDBODYEDITS", "t0"."POSTSCLOSED", "t6"."USERID" AS "USERID0", "t6"."DISPLAYNAME", "t6"."REPUTATION", "t6"."TOTALPOSTS", "t6"."QUESTIONS", "t6"."ANSWERS", "t6"."WIKIS", "t6"."POPULARPOSTS", "t6"."USERID0" AS "USERID00", "t6"."TOTALBADGES", "t6"."GOLDBADGES", "t6"."SILVERBADGES", "t6"."BRONZEBADGES"
FROM (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALEDITS", SUM(CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) IN (4, 5, 6) THEN 1 ELSE 0 END) AS "TITLEANDBODYEDITS", SUM(CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) = 10 THEN 1 ELSE 0 END) AS "POSTSCLOSED"
FROM "STACK"."PostHistory"
GROUP BY "UserId") AS "t0"
RIGHT JOIN (SELECT "t5"."USERID", "t5"."DISPLAYNAME", "t5"."REPUTATION", "t5"."TOTALPOSTS", "t5"."QUESTIONS", "t5"."ANSWERS", "t5"."WIKIS", "t5"."POPULARPOSTS", "t2"."USERID" AS "USERID0", "t2"."TOTALBADGES", "t2"."GOLDBADGES", "t2"."SILVERBADGES", "t2"."BRONZEBADGES"
FROM (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t2"
RIGHT JOIN (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", "Users"."Reputation" AS "REPUTATION", COUNT("Posts"."Id") AS "TOTALPOSTS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "QUESTIONS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "ANSWERS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "WIKIS", SUM(CASE WHEN "Posts"."ViewCount" > 100 THEN 1 ELSE 0 END) AS "POPULARPOSTS"
FROM "STACK"."Posts"
RIGHT JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id"
GROUP BY "Users"."Id", "Users"."DisplayName", "Users"."Reputation") AS "t5" ON "t2"."USERID" = "t5"."USERID") AS "t6" ON "t0"."USERID" = "t6"."USERID"