SELECT COALESCE("t8"."POSTID", "t8"."POSTID") AS "POSTID", "t8"."TITLE", "t8"."SCORE", "t8"."OWNERDISPLAYNAME", "t8"."CREATIONDATE", "t8"."COMMENTCOUNT", "t8"."ENGAGEMENTLEVEL", "t8"."UPVOTES", "t8"."DOWNVOTES"
FROM (SELECT "t6"."POSTID", "t6"."TITLE", "t6"."SCORE", "t6"."OWNERDISPLAYNAME", "t6"."CREATIONDATE", "t6"."COMMENTCOUNT", "t6"."ENGAGEMENTLEVEL", CASE WHEN "t2"."UPVOTES" IS NOT NULL THEN CAST("t2"."UPVOTES" AS INTEGER) ELSE 0 END AS "UPVOTES", CASE WHEN "t2"."DOWNVOTES" IS NOT NULL THEN CAST("t2"."DOWNVOTES" AS INTEGER) ELSE 0 END AS "DOWNVOTES"
FROM (SELECT "PostId" AS "POSTID", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES"
FROM "STACK"."Votes"
GROUP BY "PostId") AS "t2"
RIGHT JOIN (SELECT "t3"."POSTID", "t3"."TITLE", "t3"."SCORE", "t3"."OWNERDISPLAYNAME", "t3"."CREATIONDATE", COUNT("Comments"."Id") AS "COMMENTCOUNT", CASE WHEN COUNT("Comments"."Id") > 10 THEN 'High Engagement    ' WHEN COUNT("Comments"."Id") >= 1 AND COUNT("Comments"."Id") <= 10 THEN 'Moderate Engagement' ELSE 'No Engagement      ' END AS "ENGAGEMENTLEVEL"
FROM "STACK"."Comments"
RIGHT JOIN (SELECT "s1"."Id" AS "POSTID", "s1"."Title" AS "TITLE", "s1"."Score" AS "SCORE", "Users"."DisplayName" AS "OWNERDISPLAYNAME", "s1"."CreationDate" AS "CREATIONDATE", ROW_NUMBER() OVER (PARTITION BY "s1"."PostTypeId" ORDER BY "s1"."CreationDate" DESC NULLS FIRST) AS "RN"
FROM "s1"
LEFT JOIN "STACK"."Users" ON "s1"."OwnerUserId" = "Users"."Id") AS "t3" ON "Comments"."PostId" = "t3"."POSTID"
GROUP BY "t3"."POSTID", "t3"."TITLE", "t3"."SCORE", "t3"."OWNERDISPLAYNAME", "t3"."CREATIONDATE"
HAVING CASE WHEN COUNT("Comments"."Id") > 10 THEN 'High Engagement    ' WHEN COUNT("Comments"."Id") >= 1 AND COUNT("Comments"."Id") <= 10 THEN 'Moderate Engagement' ELSE 'No Engagement      ' END <> 'No Engagement      ') AS "t6" ON "t2"."POSTID" = "t6"."POSTID"
ORDER BY "t6"."SCORE" DESC NULLS FIRST, "t6"."COMMENTCOUNT" DESC NULLS FIRST
FETCH NEXT 50 ROWS ONLY) AS "t8"