SELECT COALESCE("t2"."DisplayName", "t2"."DisplayName") AS "DISPLAYNAME", "t2"."Reputation" AS "REPUTATION", "t2"."LastAccessDate" AS "LASTACCESSDATE", "t2"."Views" + "t2"."UpVotes" - "t2"."DownVotes" AS "ENGAGEMENTSCORE", "t1"."BADGECOUNT", "t1"."GOLDBADGES", "t1"."SILVERBADGES", "t1"."BRONZEBADGES", "t4"."POSTCOUNT", "t4"."QUESTIONCOUNT", "t4"."ANSWERCOUNT", "t4"."WIKICOUNT", "t4"."TOTALSCORE", "t4"."AVGVIEWCOUNT"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
RIGHT JOIN "STACK"."Users" ON "Badges"."UserId" = "Users"."Id"
GROUP BY "Users"."Id", "Users"."DisplayName") AS "t1"
INNER JOIN (SELECT *
FROM "STACK"."Users"
WHERE "Reputation" > 100 AND "LastAccessDate" > (TIMESTAMP '2024-10-01 12:34:56' - INTERVAL '1' YEAR)) AS "t2" ON "t1"."USERID" = "t2"."Id"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "POSTCOUNT", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "QUESTIONCOUNT", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "ANSWERCOUNT", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "WIKICOUNT", SUM("Score") AS "TOTALSCORE", AVG("ViewCount") AS "AVGVIEWCOUNT"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t4" ON "t2"."Id" = "t4"."OWNERUSERID"