SELECT COALESCE("USERID", "USERID") AS "USERID", "DISPLAYNAME", "POSTCOUNT", "QUESTIONCOUNT", "ANSWERCOUNT", "TOTALBADGES", "GOLDBADGES", "SILVERBADGES", "BRONZEBADGES", "UPVOTES", "DOWNVOTES"
FROM (SELECT "USERID0" AS "USERID", "DISPLAYNAME", "POSTCOUNT", "QUESTIONCOUNT", "ANSWERCOUNT", CASE WHEN "BADGECOUNT" IS NOT NULL THEN CAST("BADGECOUNT" AS BIGINT) ELSE 0 END AS "TOTALBADGES", CASE WHEN "GOLDBADGES" IS NOT NULL THEN CAST("GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "SILVERBADGES" IS NOT NULL THEN CAST("SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "BRONZEBADGES" IS NOT NULL THEN CAST("BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", "UPVOTES", "DOWNVOTES"
FROM "s1"
ORDER BY "UPVOTES" DESC NULLS FIRST, "DOWNVOTES"
FETCH NEXT 100 ROWS ONLY) AS "t8"