SELECT COALESCE("DISPLAYNAME", "DISPLAYNAME") AS "DISPLAYNAME", "GOLDBADGES", "SILVERBADGES", "BRONZEBADGES", "QUESTIONCOUNT", "ANSWERCOUNT", "TOTALSCORE", "TOTALVIEWS", "LASTACTIVITYDATE"
FROM (SELECT "DisplayName" AS "DISPLAYNAME", CASE WHEN "GOLDBADGES" IS NOT NULL THEN CAST("GOLDBADGES" AS BIGINT) ELSE 0 END AS "GOLDBADGES", CASE WHEN "SILVERBADGES" IS NOT NULL THEN CAST("SILVERBADGES" AS BIGINT) ELSE 0 END AS "SILVERBADGES", CASE WHEN "BRONZEBADGES" IS NOT NULL THEN CAST("BRONZEBADGES" AS BIGINT) ELSE 0 END AS "BRONZEBADGES", "QUESTIONCOUNT", "ANSWERCOUNT", "TOTALSCORE", "TOTALVIEWS", CAST("LASTACTIVITYDATE" AS TIMESTAMP(0)) AS "LASTACTIVITYDATE"
FROM "s1"
ORDER BY "TOTALSCORE" DESC NULLS FIRST, "DisplayName") AS "t10"