SELECT COALESCE("t9"."DISPLAYNAME", "t9"."DISPLAYNAME") AS "DISPLAYNAME", "t9"."TOTALBADGES", "t9"."GOLDBADGES", "t9"."SILVERBADGES", "t9"."BRONZEBADGES", "t9"."TOTALPOSTS", "t9"."QUESTIONS", "t9"."ANSWERS", "t9"."TOTALVIEWS", "t9"."TOTALSCORE"
FROM (SELECT "Users"."DisplayName" AS "DISPLAYNAME", "t7"."TOTALBADGES", "t7"."GOLDBADGES", "t7"."SILVERBADGES", "t7"."BRONZEBADGES", "t7"."TOTALPOSTS", "t7"."QUESTIONS", "t7"."ANSWERS", "t7"."TOTALVIEWS", "t7"."TOTALSCORE"
FROM "STACK"."Users"
INNER JOIN (SELECT "t6"."USERID", "t6"."TOTALBADGES", "t6"."GOLDBADGES", "t6"."SILVERBADGES", "t6"."BRONZEBADGES", CAST("s1"."TOTALPOSTS" AS BIGINT) AS "TOTALPOSTS", CAST("s1"."QUESTIONS" AS INTEGER) AS "QUESTIONS", CAST("s1"."ANSWERS" AS INTEGER) AS "ANSWERS", CAST("s1"."TOTALVIEWS" AS INTEGER) AS "TOTALVIEWS", CAST("s1"."TOTALSCORE" AS INTEGER) AS "TOTALSCORE"
FROM (SELECT ANY_VALUE("Users0"."Id") AS "USERID", COUNT("Badges"."Id") AS "TOTALBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Badges"."UserId" = "Users0"."Id"
GROUP BY "Users0"."Id") AS "t6"
INNER JOIN "s1" ON "t6"."USERID" = "s1"."OWNERUSERID") AS "t7" ON "Users"."Id" = "t7"."USERID"
ORDER BY "t7"."TOTALSCORE" DESC NULLS FIRST, "t7"."TOTALBADGES" DESC NULLS FIRST
FETCH NEXT 10 ROWS ONLY) AS "t9"