FROM "TPCH"."lineitem"
GROUP BY "l_orderkey") AS "t2" ON "orders"."o_orderkey" = "t2"."L_ORDERKEY"
LEFT JOIN "TPCH"."partsupp" AS "partsupp0" ON "lineitem"."l_partkey" = "partsupp0"."ps_partkey"
LEFT JOIN "TPCH"."part" ON "partsupp0"."ps_partkey" = "part"."p_partkey"
LEFT JOIN "TPCH"."customer" ON "orders"."o_custkey" = "customer"."c_custkey"