SELECT COALESCE("SUPPLIER_NAME", "SUPPLIER_NAME") AS "SUPPLIER_NAME", "TOTAL_PRODUCTS", "TOTAL_AVAILABLE_QUANTITY", "AVERAGE_SUPPLY_COST", "BRANDS_OFFERED", "CONTAINER_TYPES", "SUPPLIER_SUMMARY"
FROM (SELECT "SUPPLIER_NAME", "TOTAL_PRODUCTS", "TOTAL_AVAILABLE_QUANTITY", "AVERAGE_SUPPLY_COST", "BRANDS_OFFERED", "CONTAINER_TYPES", CONCAT('Supplier: ', "SUPPLIER_NAME", ' offers ', CAST("TOTAL_PRODUCTS" AS VARCHAR CHARACTER SET "ISO-8859-1"), ' products, with an average supply cost of $', CAST(ROUND("AVERAGE_SUPPLY_COST", 2) AS VARCHAR CHARACTER SET "ISO-8859-1"), '.') AS "SUPPLIER_SUMMARY"
FROM "s1"
ORDER BY "TOTAL_PRODUCTS" DESC NULLS FIRST) AS "t4"