SELECT COALESCE("PART_NAME", "PART_NAME") AS "PART_NAME", "SUPPLIER_NAME", "CUSTOMER_NAME", "ORDER_ID", "LINE_ITEM_COUNT", "TOTAL_EXTENDED_PRICE", "AVERAGE_DISCOUNT", "LATEST_SHIP_DATE", "REGIONS_SUPPLIED", "PART_COMMENTS"
FROM (SELECT "PART_NAME", "SUPPLIER_NAME", "CUSTOMER_NAME", "ORDER_ID", "LINE_ITEM_COUNT", "TOTAL_EXTENDED_PRICE", "AVERAGE_DISCOUNT", "LATEST_SHIP_DATE", "REGIONS_SUPPLIED", "PART_COMMENTS"
FROM "s1"
ORDER BY "TOTAL_EXTENDED_PRICE" DESC NULLS FIRST, "LINE_ITEM_COUNT"
FETCH NEXT 50 ROWS ONLY) AS "t6"