SELECT COALESCE("P_NAME", "P_NAME") AS "P_NAME", "TOTAL_QUANTITY", "AVG_PRICE", "SUMMARY", "REGION_NAME", "NATION_NAME", "SUPPLIER_NAME", "CUSTOMER_NAME"
FROM (SELECT "p_name" AS "P_NAME", "TOTAL_QUANTITY", "AVG_PRICE", CONCAT('Total Quantity: ', CAST("TOTAL_QUANTITY" AS CHAR(1) CHARACTER SET "ISO-8859-1"), ', Avg Price: ', CAST(CAST("AVG_PRICE" AS DECIMAL(10, 2)) AS VARCHAR CHARACTER SET "ISO-8859-1")) AS "SUMMARY", "REGION_NAME", "NATION_NAME", "SUPPLIER_NAME", "CUSTOMER_NAME"
FROM "s1"
ORDER BY "TOTAL_QUANTITY" DESC NULLS FIRST, "AVG_PRICE"
FETCH NEXT 10 ROWS ONLY) AS "t5"