SELECT COALESCE(t.Id, t.Id) AS Id, t.PostTypeId, t.AcceptedAnswerId, t.ParentId, t.CreationDate, t.Score, t.ViewCount, t.Body, t.OwnerUserId, t.OwnerDisplayName, t.LastEditorUserId, t.LastEditorDisplayName, t.LastEditDate, t.LastActivityDate, t.Title, t.Tags, t.AnswerCount, t.CommentCount, t.FavoriteCount, t.ClosedDate, t.CommunityOwnedDate, t.ContentLicense, Users.Id AS Id0, Users.Reputation, Users.CreationDate AS CreationDate0, Users.DisplayName, Users.LastAccessDate, Users.WebsiteUrl, Users.Location, Users.AboutMe, Users.Views, Users.UpVotes, Users.DownVotes, Users.ProfileImageUrl, Users.AccountId, Comments.Id AS Id1, Comments.PostId, Comments.Score AS Score0, Comments.Text, Comments.CreationDate AS CreationDate1, Comments.UserDisplayName, Comments.UserId, Comments.ContentLicense AS ContentLicense0
FROM Comments
RIGHT JOIN (Users INNER JOIN (SELECT *
FROM Posts
WHERE CAST(PostTypeId AS INTEGER) = 1) AS t ON Users.Id = t.OwnerUserId) ON Comments.PostId = t.Id